% This function returns and calculates points with a Lorentzian distribution when I give it a uniform distribution of points between 0 and 1
function f = lorentzian_density_function(x, A, b)
    
    %f = ((12*6^(1/2)*A*(3*3^(1/2)*((27*A^4)./(b^8*x.^4) - (64*A^8)./(b^10*x.^2)).^(1/2) - (32*A^6)/b^6 + (27*A^2)./(b^4*x.^2)).^(1/2))./(b^2*x) - (16*A^4*((16*A^4)/b^4 + 9*((2*3^(1/2)*((27*A^4)./(b^8*x.^4) - (64*A^8)./(b^10*x.^2)).^(1/2))/9 - (64*A^6)/(27*b^6) + (2*A^2)./(b^4*x.^2)).^(2/3) - (12*A^2*((2*3^(1/2)*((27*A^4)./(b^8*x.^4) - (64*A^8)./(b^10*x.^2)).^(1/2))/9 - (64*A^6)/(27*b^6) + (2*A^2)./(b^4*x.^2)).^(1/3))/b^2).^(1/2))/b^4 - (24*A^2*((2*3^(1/2)*((27*A^4)./(b^8*x.^4) - (64*A^8)./(b^10*x.^2)).^(1/2))/9 - (64*A^6)/(27*b^6) + (2*A^2)./(b^4*x.^2)).^(1/3).*((16*A^4)/b^4 + 9*((2*3^(1/2)*((27*A^4)./(b^8*x.^4) - (64*A^8)./(b^10*x.^2)).^(1/2))/9 - (64*A^6)/(27*b^6) + (2*A^2)./(b^4*x.^2)).^(2/3) - (12*A^2*((2*3^(1/2)*((27*A^4)./(b^8*x.^4) - (64*A^8)./(b^10*x.^2)).^(1/2))/9 - (64*A^6)/(27*b^6) + (2*A^2)./(b^4*x.^2)).^(1/3))/b^2).^(1/2))/b^2 - 9*((2*3^(1/2)*((27*A^4)./(b^8*x.^4) - (64*A^8)./(b^10*x.^2)).^(1/2))/9 - (64*A^6)/(27*b^6) + (2*A^2)./(b^4*x.^2)).^(2/3).*((16*A^4)/b^4 + 9*((2*3^(1/2)*((27*A^4)./(b^8*x.^4) - (64*A^8)./(b^10*x.^2)).^(1/2))/9 - (64*A^6)/(27*b^6) + (2*A^2)./(b^4*x.^2)).^(2/3) - (12*A^2*((2*3^(1/2)*((27*A^4)./(b^8*x.^4) - (64*A^8)./(b^10*x.^2)).^(1/2))/9 - (64*A^6)/(27*b^6) + (2*A^2)./(b^4*x.^2)).^(1/3))/b^2).^(1/2)).^(1/2)./(6*((2*3^(1/2).*((27*A^4)./(b^8*x.^4) - (64*A^8)./(b^10*x.^2)).^(1/2))/9 - (64*A^6)/(27*b^6) + (2*A^2)./(b^4*x.^2)).^(1/6).*((16*A^4)/b^4 + 9*((2*3^(1/2)*((27*A^4)./(b^8*x.^4) - (64*A^8)./(b^10*x.^2)).^(1/2))/9 - (64*A^6)/(27*b^6) + (2*A^2)./(b^4*x.^2)).^(2/3) - (12*A^2*((2*3^(1/2)*((27*A^4)./(b^8*x.^4) - (64*A^8)./(b^10*x.^2)).^(1/2))/9 - (64*A^6)/(27*b^6) + (2*A^2)./(b^4*x.^2)).^(1/3))/b^2).^(1/4)) - ((16*A^4)/b^4 + 9*((2*3^(1/2)*((27*A^4)./(b^8*x.^4) - (64*A^8)./(b^10*x.^2)).^(1/2))/9 - (64*A^6)/(27*b^6) + (2*A^2)./(b^4*x.^2)).^(2/3) - (12*A^2*((2*3^(1/2)*((27*A^4)./(b^8*x.^4) - (64*A^8)./(b^10*x.^2)).^(1/2))/9 - (64*A^6)/(27*b^6) + (2*A^2)./(b^4*x.^2)).^(1/3))/b^2).^(1/2)./(6*((2*3^(1/2)*((27*A^4)./(b^8*x.^4) - (64*A^8)./